import React, { useState } from 'react'
import styled from "styled-components"
import { Avatar } from '@material-ui/core'
import AccessTimeIcon from '@material-ui/icons/AccessTime';
import SearchIcon from '@material-ui/icons/Search';
import HelpOutlineIcon from '@material-ui/icons/HelpOutline';
import axios from 'axios'

function Header() {

    const [search, setSearch] = useState('');

    //! submit button
    const handleKeyPress = (e) => {
        console.log(e.key);
        const cors = 'https://cors-anywhere.herokuapp.com/';
        if(e.key === 'Enter'){
            // axios.get(cors + 'https://api.offerupnow.com/api/search/v4/feed/')
            // .then(response => {
            //     console.log(response);
            // })
        }
    }

    return (
        <HeaderContainer>
            <HeaderLeft>
              <HeaderAvatar
            //   displayName  
              />
              <AccessTimeIcon />
            </HeaderLeft>
            <HeaderSearch>
              <SearchIcon />
              <input onChange={(e) => setSearch(e.target.value)} value={search} onKeyPress={handleKeyPress} placeholder="Search" type="text"></input>
            </HeaderSearch>
            <HeaderRight>
              <HelpOutlineIcon />
            </HeaderRight>
        </HeaderContainer>
    )
}

export default Header

// Using Styled Components
const HeaderSearch = styled.div`
    flex: 0.4;
    opacity: 1;
    border-radius: 6px;
    /* background-color: #421f44; */
    background-color: #ffad14;
    text-align: center;
    display: flex;
    padding: 0 50px;
    border: 1px solid gray;

    > input {
        background-color: transparent;
        border: none;
        text-align: center;
        min-width: 30vw;
        outline: none;
        color: #faebd7;
    }
`;

const HeaderContainer = styled.div`
    display: flex;
    position: fixed;
    width: 100%;
    align-items: center;
    justify-content: space-between;
    padding: 10px 0;
    /* background-color: var(--slack-color); */
    background-color: #db8f00;
    color: #faebd7;
`;

const HeaderLeft = styled.div` //h2
    flex: 0.3;
    display: flex;
    align-items: center;
    margin-left: 20px;

    >   .MuiSvgIcon-root {
      margin-left: auto;
      margin-right: 30px;  
    }
`;

const HeaderRight = styled.div`
    flex: 0.3;
    display: flex;
    align-items: flex-end;

    :hover{
        color: red;
    }

    > .MuiSvgIcon-root {
        margin-left: auto;
        margin-right: 20px;
    }
`;

const HeaderAvatar = styled(Avatar)`
    cursor: pointer;

    :hover{
        opacity: 0.8;
    }
`;
