import styled from 'styled-components'
import Chat from './components/Chat';
import Login from './components/Login';
import Disclaimer from ''

function App() {

  return (
    <div className="app">

      <Router>
        {/* <Login/> */}
          <>
        <Header />
        <AppBody>
          <Sidebar  />
        <Switch>
          <Route exact path="/">
            <Chat/>
          </Route>
        </Switch>
        </AppBody>
      </>   
    </Router>
    </div>
  );
}

export default App;

const AppBody = styled.div`
  display: flex;
  height: 100vh;
`;

